package com.practice.ds0;
import java.util.*;
public class AnagramCount {
    int anagram_count=0;
    static List<Integer> indexList = new ArrayList<Integer>();
    static Map<Character,Integer> charCountMap = new HashMap<Character,Integer>();
    static Map<Character,Integer> charCountMapOrig = new HashMap<Character,Integer>();
    public static List<Integer> findAnagrams(String s, String p) {
        if(p.length() > s.length())
        {
            indexList.add(-1);
            return indexList;
        }
        //map for p
        for(int i =0 ; i < p.length();i++)
        {
            int count =charCountMap.getOrDefault(p.charAt(i),0);
            charCountMap.put(p.charAt(i),count+1);
        }
        //map for first p.length() chars in s
        for(int i =0; i < p.length();i++)
        {
            int count =charCountMapOrig.getOrDefault(s.charAt(i),0);
            charCountMapOrig.put(s.charAt(i),count+1);
        }
        for(int i =0; i <= s.length()-p.length();i++)
        {
            if(s.charAt(2+i)=='b'){
                System.out.println(i);
            }
            int count =0;
            int next=0;
            for(char j:charCountMap.keySet())
            {
                if(!charCountMapOrig.containsKey(j))
                    break;
                else
                if((int)charCountMap.get(j) != (int)charCountMapOrig.get(j))
                    break;
                else
                    count= count+charCountMap.get(j);
            }
            if(count == p.length())
                indexList.add(i);
            if(i == s.length()-p.length())
                next = i+p.length()-1;
            else
                next = i+p.length();

            int c =charCountMapOrig.getOrDefault(s.charAt(next),0);
            charCountMapOrig.put(s.charAt(next),c+1);
            int prev= charCountMapOrig.get(s.charAt(i));
            if(prev ==0 || prev-1 ==0)
                charCountMapOrig.remove(s.charAt(i));
            else
                charCountMapOrig.put(s.charAt(i),prev-1);
        }
        return indexList;
    }

    public static void main(String[] args) {
        String s ="";
        String p = "";
        List index=findAnagrams(s,p);
        System.out.println(index.toString());
    }
}
